import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row"; 
import Col from "react-bootstrap/Col";

const Calendar = () => {
    const weekDays : string[] =["Monday","Tuesday","Wednesday","Thursday","Friday","Saturday", "Sunday"]
    const weekInMonth : number[] = [0,1,2,3,4,5];

    const currentDate : Date = new Date();
    const spltdata : string[] = currentDate.toString().split(" ");

    function firstDayInMonth() : string {
        var indexOfFirstDayInTheMonth : number = currentDate.getDay();

        while (indexOfFirstDayInTheMonth < 6 ){
            indexOfFirstDayInTheMonth -= 7;
        }

        return weekDays[indexOfFirstDayInTheMonth];
    }

    return(
        <Container fluid>
            {weekInMonth.map(week => 
                <Row key={week}>
                    {weekDays.map(day => 
                            <Col key={day}>
                                {week === 0 ? day : "TAKI"}
                            </Col>
                        )
                    }   
                </Row>
            )}    
            {firstDayInMonth()}
        </Container>
    );
};

export default Calendar;
