import { Container, Row, Col } from "react-bootstrap";

const Calendar = () => {
    const weekDays =["Monday","Tuesday","Wednesday","Thursday","Friday","Saturday", "Sunday"]
    const weekInMonth = [0,1,2,3,4,5];

    return(
        <Container fluid>
            {weekDays.map(day => 
                <Col id={weekDays.indexOf(day).toString()} md="auto">
                    {weekInMonth.map(week => 
                        <Row id={week.toString()} md="auto">
                                {week === 0 ? day : "TAKI"}
                        </Row>
                    )}
                </Col>
            )
    }
        </Container>
    );
};

export default Calendar;
