import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row"; 
import Col from "react-bootstrap/Col";
import { useState } from "react";
import Day from "./Day";

const Calendar = () => {
    const currentDate : Date = new Date();
    const daysOfWeek : string[] = ["Sunday", "Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"]
    const weekInMonth : number[] = [0,1,2,3,4,5];
    const monthCodeList : number[] = [0,3,3,6,1,4,6,2,5,0,3,5];
    const monthCode : number = currentDate.getUTCMonth() === 0 ? monthCodeList[0] : monthCodeList[currentDate.getUTCMonth() - 1];


    
    const splitDate : string[] = currentDate.toString().split(" ");

    const [day, setDay] = useState<number>(currentDate.getDate()); // Dzień miesiąca
    const [weekday, setWeekday] = useState<number>(daysOfWeek.findIndex(day => {day.includes(splitDate[0])})); // Dzień tygodnia  


    const firstWeekDayOfMonth  = ((currentDate.getUTCFullYear() + (currentDate.getUTCFullYear() / 4)) % 7 + monthCode + (18 - Math.floor(currentDate.getUTCFullYear() / 100)) % 7 + currentDate.getDate() - currentDate.getUTCFullYear() % 4 ? 1 : 0) % 7
    
    return(
        <Container fluid>
            {weekInMonth.map(week => 
                <Row key={week}>
                    {daysOfWeek.map(day => 
                            <Col key={day}>
                                {week === 0 ? day : <Day dayInMonth={week} />}
                            </Col>
                        )
                    }   
                </Row>
            )}    
            {firstWeekDayOfMonth}     
        </Container>
    );
};

export default Calendar;
