import {ListGroup, Badge } from 'react-bootstrap';

interface DayEventListProps {
  day : number | string,
  events : string[]
}

function DayEventList( {day, events} : DayEventListProps ) {
  return (
    <>
      <Badge pill bg="info">
          {day}
      </Badge>
      {events.length !== 0 ? (<ListGroup variant="flush">
        {events.map( event => (<ListGroup.Item>{event}</ListGroup.Item>))}
      </ListGroup>): null}
    </>
  );
}

export default DayEventList;
