import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row"; 
import Col from "react-bootstrap/Col";
import { useState } from "react";
import Day from "./Day";
import { Console } from "console";

const calculateFirstDayOfMonth = (currentDay: number, currentWeekday: number): number => {
    const daysInWeek = 7;
    const daysPassed = currentDay % daysInWeek;
    const firstDayIndex = (currentWeekday - daysPassed + daysInWeek) % daysInWeek;
    return firstDayIndex;
};

const Calendar = () => {
    const daysOfWeek : string[] = ["Sunday", "Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"]
    const weekInMonth : number[] = [0,1,2,3,4,5];

    const currentDate : Date = new Date();
    const splitDate : string[] = currentDate.toString().split(" ");

    const [day, setDay] = useState<number>(currentDate.getDate()); // Dzień miesiąca
    const [weekday, setWeekday] = useState<number>(daysOfWeek.findIndex(day => {day.includes(splitDate[0])})); // Dzień tygodnia  
  
    const firstDay : number = calculateFirstDayOfMonth(day, weekday);

    function firstDayOfMonth( currentDay : number) : string {
        var calculatedDay : number = currentDay;

        while (calculatedDay > daysOfWeek.length){
            calculatedDay -= daysOfWeek.length;
        }
        
        var indexOfWeekDay : number = currentDate.getDay();
        
        return daysOfWeek[indexOfWeekDay-calculatedDay];
    }
    
    return(
        <Container fluid>
            {weekInMonth.map(week => 
                <Row key={week}>
                    {daysOfWeek.map(day => 
                            <Col key={day}>
                                {week === 0 ? day : <Day dayInMonth={week} />}
                            </Col>
                        )
                    }   
                </Row>
            )}    
            {firstDayOfMonth(day)}     
        </Container>
    );
};

export default Calendar;
