import { Container, Row, Col } from "react-bootstrap";

const Calendar = () => {
    const weekDays =["Monday","Tuesday","Wednesday","Thursday","Friday","Saturday", "Sunday"]
    const weekInMonth = [0,1,2,3,4,5];

    const Day = (week : number) =>{
        if (week === 0) return(weekDays[week])
        return ("")
    }

    return(
        <Container>
            {weekDays.map(day => 
                <Row>
                    {weekInMonth.map(week => 
                        <Col>
                                {week === 0 ? day : "TAKI"}
                        </Col>
                    )}
                </Row>
            )
    }
        </Container>
    );
};

export default Calendar;
